CREATE FUNCTION dbo.date$getGreatest
(
    @date1    datetime,
    @date2    datetime,
    @date3    datetime = NULL,
    @date4    datetime = NULL
)
RETURNS datetime
AS
  BEGIN
    RETURN (SELECT MAX(dateValue)
            FROM  ( SELECT @date1 as dateValue
                           UNION ALL 
                           SELECT @date2
                           UNION ALL 
                           SELECT @date3
                           UNION ALL
                           SELECT @date4 ) as dates);
  
  END;
GO

